class Feedcategory extends Admin_Controller
{

    function __construct()
    {
        parent::__construct();
        $this->load->model('Feedcategory_model');
    }

    function index()
    {
        $this->load->library('form_validation');
        $this->form_validation->set_rules('feedcategory', 'Category Name', 'required');

        if ($this->form_validation->run()) {
            $params = array(
                'name' => $this->input->post('feedcategory'),
                'created_by' => $this->session->userdata('id'),
            );
            $this->Feedcategory_model->add_feedcategory($params);
            $this->session->set_flashdata('success', 'Feed Category Added Successfuly');
            redirect('feedcategory');
        } else {
            $data['_view'] = 'feedcategory/index';
            $this->load->view('layouts/main', $data);
        }
    }

    function get_feedcategory_for_mixture()
    { 
        $this->db->select('feed_category.id, feed_category.name as feed_category_name');
        $this->db->where('status', ACTIVE);
        return $this->db->get('feed_category')->result_array();
    }
    function get_list()
    {
        $columns = array(
            0 => 'name',
            1 => 'id'
        );

        // This is for sorting and pagination, it will mstly stay the same
        $limit = $this->input->post('length');
        $start = $this->input->post('start');
        $order = $columns[$this->input->post('order')[0]['column']];
        $dir = $this->input->post('order')[0]['dir'];
        // This is for sorting and pagination, it will mstly stay the same end


        $totalData = $this->Feedcategory_model->get_all_feedcategory_count();

        $totalFiltered = $totalData;

        if (empty($this->input->post('search')['value'])) {
            $posts = $this->Feedcategory_model->get_all_feedcategory($limit, $start, $order, $dir);
        } else {
            $search = $this->input->post('search')['value'];

            $posts =  $this->Feedcategory_model->feedcategory_search($limit, $start, $search, $order, $dir);

            $totalFiltered = $this->Feedcategory_model->feedcategory_search_count($search);
        }

        $data = array();
        if (!empty($posts)) {
            foreach ($posts as $post) {
                $nestedData['name'] = $post->name;
                $nestedData['actions'] = "<button class='btn btn-warning btn-xs btn-edit' code='".$post->id."'>Edit</button>&nbsp;<button class='btn btn-danger btn-xs btn-delete' code='".$post->id."'>Delete</button>";

                $data[] = $nestedData;
            }
        }

        $json_data = array(
            "draw"            => intval($this->input->post('draw')),
            "recordsTotal"    => intval($totalData),
            "recordsFiltered" => intval($totalFiltered),
            "data"            => $data
        );

        echo json_encode($json_data);
    }


