    {
        $this->load->library('form_validation');
        $this->form_validation->set_rules('name', 'Category Name', 'required');

        if ($this->form_validation->run()) {
            echo $this->Feedcategory_model->update_feedcategory($id,[
                'name'=>$this->input->post('name')
            ]);
        }
    }

