    public function update($id){
        $this->load->library('form_validation');
        $this->form_validation->set_rules('land_management_data', 'Name', 'required');
        $this->form_validation->set_rules('land_management_type_id', 'Type', 'required');

        if ($this->form_validation->run()) {
            $this->LandManagementData_model->update($id,[
                'land_management_data' => $this->input->post('land_management_data'),
                'land_management_type_id' => $this->input->post('land_management_type_id'),
                'status'=>ACTIVE,
                'created_by' => $this->session->userdata('id')
            ]);
            echo 1;
        }

    }
