    public function delete_productcategory($id)
    {
        echo $this->Productcategory_model->delete_productcategory($id);
    }

    public function update_productcategory($id)
    {
        $this->load->library('form_validation');
        $this->form_validation->set_rules('name', 'label', 'required');

        if ($this->form_validation->run()) {
            $params = array(
                'name' => $this->input->post('name')
            );

            echo $this->Productcategory_model->update_productcategory($id,$params);

        }
    }
}
