    /*
     * Get all breed
     */
    function get_all_animalbreed($limit, $start, $col, $dir)
    {
        $this->db->where('breed.status!=',DELETED);
        $this->db->select('breed.*, animal_type.type_of_animal as animal_type');
        $this->db->join('animal_type', 'animal_type.id = breed.animal_type_id');
       
        $query = $this->db->limit($limit, $start)->order_by($col, $dir)->get('breed');

