    /*
     * Get brand by id
     */
    function get_brand($id)
    {
        return $this->db->get_where('brand', array('id' => $id))->row_array();
    }

    /*
     * Get all brand count
     */
    function get_all_province_count()
    {
        $query = $this->db->get('province'); 
        return $query->num_rows();
    }

    function province_by_country_id($id){
        $this->db->select('province.id, province.name as province_name');
        $this->db->where('status', ACTIVE);
        $this->db->where('country_id', $id);
        return $this->db->get('province')->result_array();
    }

    function city_by_province_id($id){
        $this->db->select('city.id, city.name as city_name');
        $this->db->where('status', ACTIVE);
        $this->db->where('province_id', $id);
        return $this->db->get('city')->result_array();
    }
    /*
     * Get all brand
     */
    function get_all_province($limit, $start, $col, $dir)
    {
        $this->db->where('province.status', ACTIVE);
        $this->db->select('province.*, country.name as country_name');
        $this->db->join('country','country.id = province.country_id');
        $query = $this->db->limit($limit, $start)->order_by($col, $dir)->get('province'); 

        if ($query->num_rows() > 0) {
            return $query->result();
        } else {
            return null;
        }
    }

    function province_search($limit,$start,$search,$col,$dir)
    {
        $this->db->where('province.status', ACTIVE);
        $this->db->select('province.*, country.name as country_name');
        $this->db->join('country','country.id = province.country_id');
        $query = $this
                ->db
                ->like('province.name',$search) 
                ->or_like('country.name',$search) 
                ->limit($limit,$start)
                ->order_by($col,$dir)
                ->get('province');
        
       
        if($query->num_rows()>0)
        {
            return $query->result();  
        }
        else
        {
            return null;
        }
    }

    function province_search_count($search)
    {
        $this->db->where('province.status', ACTIVE);
        $this->db->select('province.*, country.name as country_name');
        $this->db->join('country','country.id = province.country_id');
        $query = $this
                ->db
                ->like('province.name',$search) 
                ->or_like('country.name',$search)
                ->get('province');
    
        return $query->num_rows();
    } 

    /*
     * function to add new brand
     */
    function add_province($params)
    {
        $this->db->insert('province', $params);
        return $this->db->insert_id();
    }

    /*
     * function to update brand
     */
    function update_brand($id, $params)
    {
        $this->db->where('id', $id);
        return $this->db->update('brand', $params);
    }

    /*
     * function to delete brand
     */
    function delete_brand($id)
    {
        return $this->db->delete('brand', array('id' => $id));
    }

    function delete_province($id){
        return $this->db->where('id',$id)->update('province',['status'=>DELETED]);
    }

    function update_province($id,$data){
        return $this->db->where('id',$id)
        ->update('province',$data);
    }
}
